import React from 'react';
import { Col, Card, Button } from 'react-bootstrap';
import { FiLink } from 'react-icons/fi'; // Assuming you're using react-icons for consistency

const ProjectCard = ({ project }) => {
  return (
    <Col lg="6" className="mb-4">
      <Card>
        <Card.Body>
          <Card.Title className="text-center mb-3">
            {project.name} - {project.company}
            <a href={project.link} target="_blank" rel="noopener noreferrer" style={{ marginLeft: '10px', fontSize: '20px' }}>
              <FiLink />
            </a>
          </Card.Title>
          <Card.Text>{project.description}</Card.Text>
          {/* <div className="text-muted">Technologies: {project.technologies}</div> */}
        </Card.Body>
        <Card.Footer>
          <small className="text-muted">Technologies: {project.technologies}</small>
        </Card.Footer>
      </Card>
    </Col>
  );
};

export default ProjectCard;
